import React, {useEffect, useState} from 'react';
import {
  Image,
  StatusBar,
  StyleSheet,
  Text,
  TextInput,
  View,
  Animated,
} from 'react-native';
import {RouteProp, useNavigation} from '@react-navigation/native';
import {HStack} from '@gluestack-ui/themed';
import Ionic from 'react-native-vector-icons/Ionicons';
import {TouchableOpacity} from 'react-native';
interface StoryProps {
  route: RouteProp<
    {
      StoryContent: {
        imageUrl: string;
        username: string;
        isStoryscreen?: boolean;
      };
    },
    'StoryContent'
  >;
}
function StoryContent({route}: StoryProps) {
  useEffect(() => {
    let timer = setTimeout(() => {
     navigation.goBack();
    }, 5000);
    Animated.timing(progress, {
      toValue: 5,
      duration: 5000,
      useNativeDriver: false,
    }).start();
    return ()=>clearTimeout(timer);
  }, []);
  const [progress, setProgress] = useState(new Animated.Value(0));
  const progressAnimation = progress.interpolate({
    inputRange: [0, 5],
    outputRange: ['0%', '100%'],
  });
  const navigation = useNavigation();
  console.log('Route:', route);
  const imageUrl = route?.params?.imageUrl;
  const username = route?.params?.username;
  const isStoryscreen = route?.params?.isStoryscreen;
  // console.log('ImageUrl', imageUrl);
  return (
    <View
      style={[styles.container, isStoryscreen ? styles.blackBackground : null]}>
      {isStoryscreen && (
        <>
          <StatusBar backgroundColor={'black'} barStyle={'light-content'} />
          <View
            style={{
              height: 3,
              width: '100%',
              borderWidth: 1,
              backgroundColor: 'gray',
              position: 'absolute',
              top: 15,
            }}>
            <Animated.View
              style={{
                height: '100%',
                backgroundColor: 'white',
                width: progressAnimation,
              }}></Animated.View>
          </View>
        </>
      )}
      <>
        <HStack top={20}>
          <Image
            source={{
              uri: imageUrl,
            }}
            width={30}
            height={30}
            left={10}
            alt="UserImage"
            borderRadius={100}
          />
          <Text style={styles.text}>{username}</Text>
          <Text style={styles.hours}>11h </Text>
          {isStoryscreen && (
            <TouchableOpacity onPress={() => navigation.goBack()}>
              <Ionic
                name="close"
                size={20}
                color="white"
                position="absolute"
                left={254}
              />
            </TouchableOpacity>
          )}
        </HStack>
        <Image
          source={{uri: imageUrl}}
          style={{width: '100%', height: '80%', top: 30}}
          resizeMode="cover"
          onError={error => console.error('Image Error:', error)}
        />
      </>
      {isStoryscreen && (
        <HStack space="lg" top={20}>
          <TextInput
            placeholder="Send a message"
            style={styles.placeholder}
            placeholderTextColor="white"
            placeholderStyle={{left: 10}}
          />
          <Image
            source={{
              uri: 'data:image/png;base64,',
            }}
            width={20}
            height={20}
            top={24}
          />
          <Image
            source={{
              uri: 'https://techchahiye.com/wp-content/uploads/2020/04/Instagram-Direct-available-desktop-1200x900.png',
            }}
            width={30}
            height={30}
            top={20}
          />
        </HStack>
      )}
    </View>
  );
}

export default StoryContent;
const styles = StyleSheet.create({
  blackBackground: {
    backgroundColor: '#000000',
  },
  container: {
    flex: 1,
    backgroundColor: 'white',
  },
  text: {
    color: 'white',
    fontSize: 16,
    left: 20,
    top: 6,
  },
  hours: {
    color: 'white',
    fontSize: 14,
    left: 30,
    top: 8,
  },
  placeholder: {
    padding: 5,
    height: 45,
    width: 300,
    top: 16,

    borderRadius: 50,
    borderWidth: 2,
    borderColor: '#272626',
    color: 'white',
    fontSize: 16,
  },
});
